export const style = {
  textDecorationLine: '',
};

export function handleCheckbox(style) {
  style.textDecorationLine === ''
    ? style.textDecorationLine = 'line-through'
    : style.textDecorationLine = '';
  console.log(style.textDecorationLine);
}
