export const style = {
  textDecorationLine: '',
};

export function handleCheckbox(estilo) {
  if (estilo.textDecorationLine === '') {
    estilo.textDecorationLine = 'line-through';
  } else {
    estilo.textDecorationLine = '';
  }
}
