import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { foodDetailAPI } from '../services/foodAPI';
import { drinkDetailAPI } from '../services/drinkAPI';

function RecipeDetails({ type, id }) {
  const [detail, setDetail] = useState([]);

  async function getFoodDetails() {
    const requisicao = await foodDetailAPI(id);
    console.log(requisicao);
    return requisicao;
  }

  useEffect(() => {
    if (type === 'foods') {
      getFoodDetails;
    } else {
      setDetail(drinkDetailAPI(id));
      console.log(detail);
    }
  }, []);

  return (
    <div>
      <h1>{detail.strMeal}</h1>
    </div>
  );
}

RecipeDetails.propTypes = {
  type: PropTypes.string.isRequired,
  id: PropTypes.string.isRequired,
};

export default RecipeDetails;
