import React, { useEffect, useState, useContext } from 'react';
import PropTypes from 'prop-types';
import { fetchMeals } from '../services/foodAPI';
import fetchDrinks from '../services/drinkAPI';
import Card from './Card';
import AppContext from '../context/AppContext';

function Recipes({ type }) {
  const MAX_RECIPES = 12;

  const { firstRecipes,
    setFirstRecipes,
  } = useContext(AppContext);
  async function fetchMealsAPI() {
    const meals = await fetchMeals();
    setFirstRecipes(meals.splice(0, MAX_RECIPES));
  }

  async function fetchDrinksAPI() {
    const drinks = await fetchDrinks();
    setFirstRecipes(drinks.splice(0, MAX_RECIPES));
  }

  useEffect(() => {
    if (type === 'foods') {
      fetchMealsAPI();
    } else {
      fetchDrinksAPI();
    }
  }, []);

  function mealsMap(meal, index) {
    return (
      <Card
        key={ meal.idMeal }
        name={ meal.strMeal }
        thumb={ meal.strMealThumb }
        index={ index }
      />
    );
  }

  function drinksMap(drink, index) {
    return (
      <Card
        key={ drink.idDrink }
        name={ drink.strDrink }
        thumb={ drink.strDrinkThumb }
        index={ index }
      />
    );
  }

  return (
    <div>
      {
        type === 'foods'
          ? firstRecipes.map((meal, index) => mealsMap(meal, index))
          : firstRecipes.map((drink, index) => drinksMap(drink, index))
      }
    </div>
  );
}

Recipes.propTypes = {
  type: PropTypes.string,
}.isRequired;

export default Recipes;
