import React, { useState } from 'react';
import PropTypes from 'prop-types';
import AppContext from './AppContext';

function AppProvider({ children }) {
  const [title, setTitle] = useState('');
  const [searchInput, setSearchInput] = useState('');
  const [renderCards, setRenderCards] = useState([]);
  const [id, setId] = useState('');
  const [detail, setDetail] = useState([]);
  const [doneRecipe, setDoneRecipe] = useState(false);
  const [startedRecipe, setStartedRecipe] = useState(false);
  const [globalRecipes, setGlobalRecipes] = useState({});
  const [tipo, setTipo] = useState();
  const [idProgress, setidProgress] = useState();
  const [alert, setAlert] = useState(false);
  const [favorites, setFavorites] = useState([]);
  const [doneRecipes, setDoneRecipes] = useState([]);
  const [favorited, setFavorited] = useState(false);

  const addFirstFavorite = (type) => {
    localStorage.setItem('favoriteRecipes', JSON.stringify([{
      id: type === 'foods' ? detail[0].idMeal : detail[0].idDrink,
      type: type === 'foods' ? 'food' : 'drink',
      nationality: detail[0].strArea ? detail[0].strArea : '',
      category: detail[0].strCategory,
      alcoholicOrNot: type === 'foods' ? '' : detail[0].strAlcoholic,
      name: type === 'foods' ? detail[0].strMeal : detail[0].strDrink,
      image: type === 'foods' ? (
        detail[0].strMealThumb) : (detail[0].strDrinkThumb),
    }]));
  };
  const addNewFavorite = (type, favoritesLocalStorage) => {
    localStorage.setItem('favoriteRecipes', JSON.stringify([
      ...favoritesLocalStorage,
      { id: detail[0].idMeal ? detail[0].idMeal : detail[0].idDrink,
        type: type === 'foods' ? 'food' : 'drink',
        nationality: detail[0].strArea ? detail[0].strArea : '',
        category: detail[0].strCategory,
        alcoholicOrNot: type === 'foods' ? '' : detail[0].strAlcoholic,
        name: type === 'foods' ? detail[0].strMeal : detail[0].strDrink,
        image: type === 'foods' ? (
          detail[0].strMealThumb) : (detail[0].strDrinkThumb),
      }]));
  };
  const handleFavorite = (type, idr) => {
    const favoritesLocalStorage = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favorited === true) {
      localStorage.setItem('favoriteRecipes', JSON.stringify(
        favoritesLocalStorage.find((item) => item.id === idr),
      ));
      setFavorited(false);
    }
    if (favorited === false) {
      setFavorited(true);
      if (favoritesLocalStorage === null) { addFirstFavorite(type); }
      if (favoritesLocalStorage !== null) { addNewFavorite(type, favoritesLocalStorage); }
    }
  };

  const getFavoriteLocalStorage = (idr) => {
    const favoritesLocalStorage = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favoritesLocalStorage !== null) {
      const boolean = favoritesLocalStorage
        .find((item) => item.id === idr);
      if (boolean === true) { setFavorited(true); }
    }
  };

  const context = {
    idProgress,
    setidProgress,
    tipo,
    setTipo,
    title,
    setTitle,
    searchInput,
    setSearchInput,
    renderCards,
    setRenderCards,
    globalRecipes,
    setGlobalRecipes,
    id,
    setId,
    detail,
    setDetail,
    doneRecipe,
    setDoneRecipe,
    startedRecipe,
    setStartedRecipe,
    alert,
    setAlert,
    favorites,
    setFavorites,
    doneRecipes,
    setDoneRecipes,
    handleFavorite,
    getFavoriteLocalStorage,
  };

  return (
    <AppContext.Provider value={ context }>
      { children }
    </AppContext.Provider>
  );
}

AppProvider.propTypes = {
  children: PropTypes.node,
}.isRequired;

export default AppProvider;
