import React, { useEffect, useContext } from 'react';
import PropTypes from 'prop-types';
import Carousel from 'react-multi-carousel';
import { foodDetailAPI } from '../services/foodAPI';
import AppContext from '../context/AppContext';
import { drinkDetailAPI } from '../services/drinkAPI';

function RecipeDetails({ type, id }) {
  const {
    detail,
    setDetail,
  } = useContext(AppContext);
  async function getFoodDetails() {
    const { meals } = await foodDetailAPI(id);
    setDetail(meals);
  }
  async function getDrinkDetails() {
    const response = await drinkDetailAPI(id);
    setDetail(response);
  }

  useEffect(() => {
    if (type === 'foods') {
      getFoodDetails();
    } else if (type === 'drinks') {
      getDrinkDetails();
    }
  }, []);

  const handleIngMeaDrink = (data) => {
    const filteredIngredients = data.filter((key) => key[0]
      .includes('strIngredient') && (key[1] !== null && key[1] !== ''));
    const ingArray = filteredIngredients.reduce((acc, value) => [...acc, value[1]], []);
    const filteredMeasures = data.filter((key) => key[0]
      .includes('strMeasure') && (key[1] !== null && key[1] !== ' '));
    const meaArray = filteredMeasures.reduce((acc, value) => [...acc, value[1]], []);
    const arrayToMap = ingArray.map((ing, index) => `- ${ing} - ${meaArray[index]}`);

    return (
      arrayToMap.map((string, index) => (
        <li
          key={ index }
          data-testid={ `${index}-ingredient-name-and-measure` }
        >
          {string}
        </li>)));
  };

  const handleYoutube = (url) => {
    const newUrl = url.includes('watch') ? url.replace('watch?v=', 'embed/') : url;
    return (
      <div>
        <iframe
          width="420px"
          height="360px"
          src={ newUrl }
          frameBorder="0"
          data-testid="video"
          allow="autoplay; encrypted-media"
          allowFullScreen
          title="video"
        />
      </div>
    );
  };

  if (type === 'foods') {
    return (
      <div>
        {detail.map((test, index) => (
          <div key={ index }>
            <h1 data-testid="recipe-title">{test.strMeal}</h1>
            <img
              src={ test.strMealThumb }
              alt={ test.strMeal }
              data-testid="recipe-photo"
              width="420"
              height="345"
            />
            <h4>Category</h4>
            <p data-testid="recipe-category">
              {test.strCategory}
            </p>
            <h4> Intructions </h4>
            <p data-testid="instructions">
              {test.strInstructions}
            </p>
            <h4>YouTube Video</h4>
            {handleYoutube(test.strYoutube)}
            <h4>Ingridients</h4>
            <ul>
              {handleIngMeaDrink(Object.entries(test))}
            </ul>

          </div>

        ))}
      </div>
    );
  } if (type === 'drinks') {
    return (
      <div>
        {detail.map((test, index) => (
          <div key={ index }>
            <h1 data-testid="recipe-title">{test.strDrink}</h1>
            <img
              src={ test.strDrinkThumb }
              alt={ test.strDrink }
              data-testid="recipe-photo"
              width="420"
              height="345"
            />
            <h4>Category</h4>
            <p data-testid="recipe-category">
              {test.strCategory}
            </p>
            <h4> Intructions </h4>
            <p data-testid="instructions">
              {test.strInstructions}
            </p>

            <h4>Ingridients</h4>
            <ul>
              {handleIngMeaDrink(Object.entries(test))}
            </ul>

          </div>

        ))}
      </div>
    );
  }
}

RecipeDetails.propTypes = {
  type: PropTypes.string.isRequired,
  id: PropTypes.string.isRequired,
};

export default RecipeDetails;
