import React, { useEffect, useState } from 'react';
import propTypes from 'prop-types';
import { Carousel } from 'react-responsive-carousel';
import { fetchFoodsList } from '../services/foodAPI';
import 'react-multi-carousel/lib/styles.css';

const SIX = 6;

function DetailCards({ typeOf }) {
  const [recomended, setRecomended] = useState(undefined);

  const typeInverted = (typeOf === 'foods') ? 'drinks' : 'foods';

  useEffect(() => {
    const getRecomended = async () => {
      const currRecomended = await fetchFoodsList(typeInverted);
      setRecomended(currRecomended);
    };
    getRecomended();
  }, []);
  console.log(recomended);
  // const { meals } = recomended;
  // console.log(meals);
  // const sliced = meals.slice(0, SIX);
  // sliced.map((teste) => console.log(teste.strMeal));
  // ok
  const recomendedRenderer = (typeR, data) => {
    if (typeR === 'foods') {
      const { meals } = data;
      const sliced = meals.slice(0, SIX);
      console.log(sliced);
      const returnArray = sliced.map((recipe, index) => (
        <Carousel key={ index }>
          <button
            key={ recipe.idMeal }
            id={ recipe.idMeal }
            type="button"
            data-testid={ `${index}-recomendation-card` }
          >
            <img
              src={ recipe.strMealThumb }
              alt={ recipe.strMeal }
              id={ recipe.idMeal }
              width="200px"
              data-testid={ `${index}-card-img` }
            />
            <p
              id={ recipe.idMeal }
              data-testid={ `${index}-recomendation-title` }
            >
              {recipe.strMeal}
            </p>
          </button>
        </Carousel>
      ));
      return returnArray;
    } if (typeR === 'drinks') {
      const { drinks } = data;
      const sliced = drinks.slice(0, SIX);
      console.log(sliced);
      const returnArray = sliced.map((recipe, index) => (
        <Carousel key={ index }>
          <button
            key={ recipe.idDrink }
            id={ recipe.idDrink }
            type="button"
            data-testid={ `${index}-recomendation-card` }
          >
            <img
              src={ recipe.strDrinkThumb }
              alt={ recipe.strDrink }
              id={ recipe.idDrink }
              width="200px"
              data-testid={ `${index}-card-img` }
            />
            <p
              id={ recipe.idDrink }
              data-testid={ `${index}-recomendation-title` }
            >
              {recipe.strDrink}
            </p>
          </button>
        </Carousel>
      ));
      console.log(returnArray);
      return returnArray;
    }
  };
  console.log(typeInverted);
  console.log(recomended.meals);
  return (
    <div>
      { () => { recomendedRenderer(typeInverted, recomended); } }
    </div>

  );
}

DetailCards.propTypes = {
  typeOf: propTypes.string,
};

DetailCards.defaultProps = {
  typeOf: '',
};

export default DetailCards;
